import React, { useState, useRef } from 'react';
import axios from 'axios';

function ContactImport() {
  const fileInputRef = useRef(null);

  const handleImportClick = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = async (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        await axios.post('http://localhost:5000/api/import-contacts', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        console.log('Imported contacts successfully');
        // Reload the page after import is done
        window.location.reload();
      } catch (error) {
        console.error('Error importing contacts:', error);
      }
    }
  };

  return (
    <div>
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />
      <button onClick={handleImportClick}>Import Contacts</button>
    </div>
  );
}

export default ContactImport;
