import React, { useState } from 'react';
import axios from 'axios';

function ContactImport() {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    handleImport();
  };

  const handleImport = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await axios.post('http://localhost:5000/api/import-contacts', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        console.log('Imported contacts:', response.data);
      } catch (error) {
        console.error('Error importing contacts:', error);
      }
    }
  };

  return (
    <div>
      <input
        type="file"
        id="fileInput" // Add an id to the file input
        onChange={handleFileChange}
        style={{ display: 'none' }} // Hide the default file input
      />
      <label htmlFor="fileInput" className="import-button">
        Import Contacts
      </label>
    </div>
  );
}

export default ContactImport;
