import React, { useState } from 'react';
import axios from 'axios';

function ContactImport({onDataImport}) {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleImport = async () => {
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:5000/api/import-contacts', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Imported contacts:', response.data);
      onDataImport(response.data);
    } catch (error) {
      console.error('Error importing contacts:', error);
    }
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleImport}>Import Contacts</button>
    </div>
  );
}

export default ContactImport;
