import React, { useState } from 'react';
import axios from 'axios';

function ContactImport() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false); // Add a loading state

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleImport = async () => {
    if (selectedFile && !isLoading) {
      setIsLoading(true); // Set loading to true to prevent multiple imports

      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await axios.post('http://localhost:5000/api/import-contacts', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        console.log('Imported contacts:', response.data);
      } catch (error) {
        console.error('Error importing contacts:', error);
      } finally {
        setIsLoading(false); // Reset loading state after import is done
      }
    }
  };

  return (
    <div>
      <input
        type="file"
        id="fileInput" // Add an id to the file input
        onChange={handleFileChange}
        style={{ display: 'none' }} // Hide the default file input
      />
      <label htmlFor="fileInput" className="import-button">
        {isLoading ? 'Importing...' : 'Import Contacts'}
      </label>
    </div>
  );
}

export default ContactImport;
